import { HttpClient } from "@angular/common/http";
import { Injectable } from "@angular/core"
import { Router } from "@angular/router";
import { catchError, tap } from "rxjs";
import { LocalStorageService } from "./localstorage.service";

@Injectable()
export class AuthService {

    constructor(private http: HttpClient, private storage: LocalStorageService, private router: Router) {}
    isLoggedIn: boolean = false

    userLogin(username: string, password: string) {

        this.http.
        post('http://127.0.0.1:8000/api/user/token/',
        {username, password}
        ).subscribe(
            responseData => {

              // console.log(responseData);
              const string_response = JSON.stringify(responseData);
              this.storage.set('Token', string_response)
              let val = string_response.slice(string_response.indexOf(":") + 2, string_response.length-2)
              val = "Token " + val
              let headers = new HttpHeaders()
              headers = headers.set('Authorization', val);
              



            });;
    }

    userRegister(username: string, email: string, password: string) {

    }

    userLogout(username: string) {
        this.isLoggedIn = false
        this.storage.remove(username)
    }

}
