            <div class="card" style="width: 18rem;">
                <!-- <img src="../../plus.png" class="card-img-top" alt="..."> -->
                <div class="card-body">
                  <h5 class="card-title">{{user.name}}</h5>
                  <p class="card-text">Username: {{user.username}}</p>
                  <p class="card-text">Email: {{user.email}}</p>

                </div>
              </div>
        </div>
    </div>
</div>
