import { HttpEvent, HttpHandler, HttpHeaders, HttpInterceptor, HttpRequest } from "@angular/common/http";
import { Injectable } from "@angular/core";
import { Observable } from "rxjs";
import { LocalStorageService } from "./localstorage.service";


@Injectable({providedIn: 'root'})
export class AuthInterceptorService implements HttpInterceptor {

    constructor(private storage: LocalStorageService,
                private auth) {}

    intercept(req: HttpRequest<any>, next: HttpHandler): Observable<HttpEvent<any>> {

        if(req.url==='http://127.0.0.1:8000/api/user/token/') {
            console.log("Header is not appended for getting token.")
            return next.handle(req);
        }

        else {

            let string_response = this.storage.get('Token')!

            let value = string_response.slice(string_response.indexOf(":") + 2, string_response.length-2)
            value = "Token " + value
            let header = new HttpHeaders()
            header = header.set('Authorization', value);
            console.log("Except for getting token, headers should be attached everywhere.")



            const modifiedReq = req.clone({headers: header})

            return next.handle(modifiedReq);


        }


    }

}
