import { HttpClient, HttpHeaders } from "@angular/common/http";
import { Injectable } from "@angular/core";
import { LocalStorageService } from "./localstorage.service";

@Injectable()
export class HttpService {

    constructor(private http: HttpClient, private storage: LocalStorageService) {}


    fetchPosts() {

        let string_response = this.storage.get('Token')!

        let value = string_response.slice(string_response.indexOf(":") + 2, string_response.length-2)
        value = "Token " + value
        let header = new HttpHeaders()
        header = header.set('Authorization', value);

            

            return this.http.get('http://127.0.0.1:8000/api/tweet/',
            {headers: new HttpHeaders({'Authorization': this.storage.get('Token')!})})
        }


}
