      // value = "Token " + value
      // let header = new HttpHeaders()

      // header = header.set('Authorization', value);
      console.log(
        'Except for getting token, headers should be attached everywhere.'
      );

    //   this.authService.user.subscribe((userData) => {
    //     // console.log(userData.token)
    //     let header = new HttpHeaders();
    //     let val = 'Token ' + userData.token;
    //     header = header.set('Authorization', val);
    //     console.log(header);
    //     // console.log(userData.token);
    //     const modifiedReq = req.clone({
    //       headers: header
    //     });

    //     console.log(modifiedReq);
    //     console.log(modifiedReq.headers.get('Authorization'))
    //     return next.handle(modifiedReq);

    //   });

     this.authService.user.pipe(take(1), )



    }
    console.log('this code is also working');

    return next.handle(req);
  }
}
