import { Injectable } from "@angular/core";


@Injectable()
export class LocalStorageService {

    set(key: string, value: string) {
        sessionStorage.setItem(key, value)
    }

    get(key: string) {
        sessionStorage.getItem(key);
    }

    remove(key: string) {
        localStorage.removeItem(key);
    }

}
