import { HttpClient } from "@angular/common/http";
import { Injectable } from "@angular/core"
import { LocalStorageService } from "./localstorage.service";
import { map } from "rxjs/operators";
@Injectable()
export class AuthService {

    constructor(private http: HttpClient, private storage: LocalStorageService) {}

    userLogin(username: string, password: string) {

        this.http.
        post('http://127.0.0.1:8000/api/user/token/',
        {username, password}
        )
        .subscribe(responseData => {

            const string_response = JSON.stringify(responseData)
            const value = string_response.slice(string_response.indexOf(":")+1,)
            this.storage.set(username, value)
            console.log(this.storage.get(username))


        });

    }

    userRegister(username: string, email: string, password: string) {

    }

}
