import { HttpClient } from '@angular/common/http';
import { EventEmitter, Injectable } from '@angular/core';
import { catchError, Subject, tap } from 'rxjs';
import { LocalStorageService } from './localstorage.service';
import { User } from './models/user.model';

@Injectable()
export class AuthService {
  user = new Subject<User>();
  constructor(private http: HttpClient, private storage: LocalStorageService) {}

  isLoggedIn = false;

  userLogin(username: string, password: string) {
    return this.http
      .post('http://127.0.0.1:8000/api/user/token/', { username, password })
      .pipe(
        tap((resData: User) => {
          const user = new User(resData.username, resData.email, resData.token);
        })
      )
      .subscribe((resData) => {
        console.log(resData);
        this.isLoggedIn = true;
        this.user.next(user);

        console.log('user is ' + this.isLoggedIn);
      });
  }

  userRegister(username: string, email: string, password: string) {
    this.http
      .post('http://127.0.0.1:8000/api/user/create/', {
        username,
        email,
        password,
      })
      .subscribe((userData) => {
        console.log('user created');
      });
    this.http
      .post('http://127.0.0.1:8000/api/user/token/', { username, password })
      .pipe(
        tap((resData: User) => {
          const user = new User(resData.username, resData.email, resData.token);
          this.user.next(user);
        })
      );
  }

  userLogout(username: string) {
    this.isLoggedIn = false;
    this.storage.remove(username);
  }
}
