import { HttpClient } from "@angular/common/http";
import { Injectable } from "@angular/core"
import { Router } from "@angular/router";
import { catchError } from "rxjs";
import { LocalStorageService } from "./localstorage.service";

@Injectable()
export class AuthService {

    constructor(private http: HttpClient, private storage: LocalStorageService, private router: Router) {}
    isLoggedIn: boolean = false

    userLogin(username: string, password: string) {

        return this.http.
        post('http://127.0.0.1:8000/api/user/token/',
        {username, password}
        ).pipe(catchError());
    }

    userRegister(username: string, email: string, password: string) {

    }

    userLogout(username: string) {
        this.isLoggedIn = false
        this.storage.remove(username)
    }

}
