import { HttpEvent, HttpHandler, HttpHeaders, HttpInterceptor, HttpRequest } from "@angular/common/http";
import { Injectable } from "@angular/core";
import { Observable } from "rxjs";
import { LocalStorageService } from "./localstorage.service";


@Injectable({providedIn: 'root'})
export class AuthInterceptorService implements HttpInterceptor {

    constructor(private storage: LocalStorageService) {}

    intercept(req: HttpRequest<any>, next: HttpHandler): Observable<HttpEvent<any>> {

        if(req.url===' http://127.0.0.1:8000/api/user/token') {
            return next.handle(req);
        }

        const modifiedReq = req.clone({headers: req.headers.append('Authorization', this.storage.get('token'))})

        req.headers.set(HttpHeaders: {'Authorization': this.storage.get('token')})

        return next.handle(req);


    }

}
